import { cloneElement } from 'react'
const style = {
    width: '100%',
    maxWidth: 1200,
    marginLeft: 'auto',
    marginRight: 'auto'
}
const Container = ({ children, renderer }) => {
    return cloneElement(renderer, {
        style: Object.assign({}, renderer.props.style, style),
        children
    })
}

export default Container;
