const style = {
    width: '100%',
    maxWidth: 1200,
    marginLeft: 'auto',
    marginRight: 'auto'
}
const Container = ({ children, comp: Comp = 'div' }) => {
    return <Comp style={style}>{children}</Comp>
}

export default Container;
